import React, { Component } from 'react';
import { Table, Tooltip, Icon } from 'antd';
class Hotel extends Component {
    state = {
        dataSource: [],
        loading: false
    }
    componentDidMount() {
        this.setState({
            loading: true
        })
        let _self = this;
        setTimeout( () => {
            _self.setState({
                loading: false,
                dataSource: [
                    {
                        key: '1',
                        name: '胡彦斌',
                        age: 32,
                        address: '西湖区湖底公园1号',
                    },
                    {
                        key: '2',
                        name: '胡彦祖',
                        age: 42,
                        address: '西湖区湖底公园1号',
                    },
                ]
            })
        },2000)

    }
    toEdit = (value) => {
        console.log('edit', value)
    }

    render() {
        const { dataSource , loading} = this.state;
        const columns = getColums({
            toEdit: this.toEdit
        });

        return (
            <>
                <Table dataSource={dataSource} columns={columns} loading={loading} />;
            </>
        )
    }
}
export default Hotel;

const getColums = (actions) => {
    return (
        [
            {
                title: '姓名',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: '年龄',
                dataIndex: 'age',
                key: 'age',
            },
            {
                title: '住址',
                dataIndex: 'address',
                key: 'address',
            },
            {
                title: '操作',
                render: (record, text) => {
                    return (
                        <div className="optIcon">
                            <Tooltip title='编辑' >
                                <Icon type="edit" onClick={() => actions.toEdit(record)} />
                            </Tooltip>
                        </div>
                    )
                }
            }
        ]
    )
}
