import React, { Component } from 'react';
import { Table, Tooltip, Icon } from 'antd';
class Hotel extends Component {
    defaultSortOrder = {
        order: 'descend',
        field: 'name',
        columnKey: 'name'
    }
    state = {
        dataSource: [],
        loading: false,
        sortedInfo: this.defaultSortOrder,
        total:0,
        current: 1,
        pageSize: 10,
    }
    componentDidMount() {
        this.setState({
            loading: true
        })
        let _self = this;
        setTimeout(() => {   //setTimeout仅仅是为了显示loading效果，后续会通过ajax来代替;
            _self.getData();
        }, 2000)

    }
    getData = () => {
        // ajax.then( () => {
        //     ...
        // })
        this.setState({
            loading: false,
            dataSource: [
                {
                    id: '1',
                    name: '香格里拉',
                    tel: '028-81234566',
                    addr: '西湖区湖底公园1号',
                    created: '2020-04-08 19:20:30'
                },
                {
